import React, { Component } from "react";
import { Container, Content, Accordion, Icon} from "native-base";
const dataArray = [
  { title: "DONNING THE FACE SHIELD", content:  "1) Put on a full face shield over the N95 respirator or face mask to provide additional protection to the front and sides of the face, including skin and eyes. 

 2) Hold on to the face shield with both hands, expand the elastic with your thumbs. 

 3) Place the elastic behind your head, so that the foam rests on your forehead. 

 4) Once the shield is situated, check to make sure it covers the front and sides of the face and no areas are left uncovered."} ,
  { title: "DISPOSING THE FACE SHIELD", content: "In this step, you will take off your face shield. 

 1) To do this, tilt your head forward slightly, grab the strap at the temples 

 2) Pull it forward and over your head, which will let the face shield fall from your face. 

 3) Dispose of the face shield in an appropriate manner which reduces chances of infection. 

 4) Always clean hands with soap for at least 20 seconds and use hand sanitizer after touching used face shields.In this step, you will take off your face shield. " },
  { title: "CLEANING THE FACE SHIELD", content: "Feedback from physicians and infection control groups suggests that the Face Shield product is not suitable for reuse even with appropriate cleaning in between patients. 

 Each hospital should determine appropriate use / reuse protocols based on a review of the product by the hospital's Infection Control group. 

 Face shields delivered to facilities should be considered non-sterile. Consult the CDIC for the latest guidance on disinfectants known to be effective against COVID- 19. 

 The following cleaning materials may be appropriate to clean only the plastic visor: 
 1) Soap-water 
 2) Isopropanol (70%) wipe 

 The following are not compatible with the visor and should not be used to clean face shields: 
 1) Hydrogen peroxide vapor sterilization 
 2) Autoclave 
 3)Soak in isopropanol or organic solvent 

 For dealing with highly infectious situations, it is recommended that the face shields are swapped out every 5 hours." },
  { title: "STORING THE FACE SHIELD", content: "1) Face shields should be left in their sealed packaging in a dark area for 72 hours after manufacture and before use. 

 2) This is to ensure that the shields do not hold any contaminants. 

 3) Store in a protected, dry, and cool storage location." },
];
export default class AccordionHeaderContentStyleExample extends Component {
  render() {
    return (
      <Container>
        <Content padder>
          <Accordion
            dataArray={dataArray} Icon = "add"
            headerStyle={{ backgroundColor: "#b7daf8" }}
            contentStyle={{ backgroundColor: "#ddecf8" }}
          />
        </Content>
      </Container>
    );
  }
}
